import { useSelector, useDispatch } from 'react-redux';
import {useState} from 'react';
import classes from './Counter.module.css';

const Counter = () => {
  const [hidden, setHidden]= useState(true);
  const dispatch = useDispatch();
  const counter = useSelector(state => state.counter);


const increamentHandler = ()=>{
  dispatch({type:"INCREAMENT"});
  };
  const increamentHandlerBy5 = ()=>{
    dispatch({type:"INCREAMENTBY5", value:100000});
    };

  const decreamentHandler = ()=>{
   dispatch({type:"DECREAMENT"});
  };
  const toggleCounterHandler = () => {setHidden(false)};

  return (
    <main className={classes.counter}>

      <h1>Redux Counter</h1>
      {hidden&& <p>show what yo got</p>}
      <div className={classes.value}>{counter}</div>
      <div>
        <button onClick={increamentHandler}>INCREAMENT</button>
        <button onClick={increamentHandlerBy5}>ICREASE BY FIVE</button>
        <button onClick={decreamentHandler}>DECREAMENT</button>
      </div>
      <button onClick={toggleCounterHandler}>Toggle Counter</button>
    </main>
  );
};

export default Counter;
